"const allJobs = $input.all();
const results = [];
const seen = new Set();

for (const item of allJobs) {
  const job = item.json;
  const link = job.link;
  if (!link || seen.has(link)) continue;
  seen.add(link);

  const text = `${job.title || ''} ${job.description || ''} ${job.location || ''}`.toLowerCase();
  if (!text.includes('remote') && !text.includes('homeoffice')) continue;

  const company = job.company_name || '';
  const title = job.title || '';

  let searchQuery = '';
  if (company) {
    searchQuery = `\"${company}\" Kontakt Telefon Email Impressum`;
  } else {
    searchQuery = `\"${title}\" Kontakt Telefon Email`;
  }

  const jobDataForOutput = {
    ...job,
    link,
    searchQuery,
    contactName: '',
    contactTitle: '',
    contactPhone: '',
    contactEmail: ''
  };

  results.push({ json: jobDataForOutput });
}

return results;
